{
  "version": "1.0",
  "state": {
    "name": {
      "type": "string",
      "initial": ""
    },
    "email": {
      "type": "string",
      "initial": ""
    },
    "submitted": {
      "type": "boolean",
      "initial": false
    }
  },
  "actions": [
    {
      "name": "updateName",
      "steps": [
        {
          "do": "set",
          "target": "name",
          "value": {
            "expr": "param",
            "name": "event",
            "path": "target.value"
          }
        }
      ]
    },
    {
      "name": "updateEmail",
      "steps": [
        {
          "do": "set",
          "target": "email",
          "value": {
            "expr": "param",
            "name": "event",
            "path": "target.value"
          }
        }
      ]
    },
    {
      "name": "submit",
      "steps": [
        {
          "do": "if",
          "condition": {
            "expr": "bin",
            "op": "&&",
            "left": {
              "expr": "bin",
              "op": "!=",
              "left": {
                "expr": "state",
                "name": "name"
              },
              "right": {
                "expr": "lit",
                "value": ""
              }
            },
            "right": {
              "expr": "bin",
              "op": "!=",
              "left": {
                "expr": "state",
                "name": "email"
              },
              "right": {
                "expr": "lit",
                "value": ""
              }
            }
          },
          "then": [
            {
              "do": "set",
              "target": "submitted",
              "value": {
                "expr": "lit",
