{
  "version": "1.0",
  "state": {
    "todos": {
      "type": "list",
      "initial": []
    },
    "newTodo": {
      "type": "string",
      "initial": ""
    }
  },
  "actions": [
    {
      "name": "setNewTodo",
      "steps": [
        {
          "do": "set",
          "target": "newTodo",
          "value": {
            "expr": "var",
            "name": "value"
          }
        }
      ]
    },
    {
      "name": "addTodo",
      "steps": [
        {
          "do": "update",
          "target": "todos",
          "operation": "push",
          "value": {
            "expr": "state",
            "name": "newTodo"
          }
        },
        {
          "do": "set",
          "target": "newTodo",
          "value": {
            "expr": "lit",
            "value": ""
          }
        }
      ]
    },
    {
      "name": "removeTodo",
      "steps": [
        {
          "do": "update",
          "target": "todos",
          "operation": "remove",
          "value": {
            "expr": "var",
